import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import EmployeeService from "../services/EmployeeService";

const UpdateEmployee = () => {
  const [id] = useParams();

  const [employee, setEmployee] = useState({
    id: id,
    firstName: "",
    lastName: "",
    emailId: "",
  });

  const handleChange = (e) => {
    const value = e.target.value;
    setEmployee({ ...employee, [e.target.name]: value });
  };

  useEffect(() => {
    const fetchData = async () => {
    try {
        const response = await EmployeeService.getEmployeeByID(id);
        setEmployee(response);
    } catch (error) {
        console.log(error);
    }
};
fetchData();
  }, []);

  const updateEmployee = (e) => {
    e.preventDefault();
  };

  return (
    <div className="flex max-w-2xl mx-auto shadow border-b justify-center">
      <div className="px-8 py-8">
        <div className="font-thin text-2xl tracking-wider">
          <h1>Update Employee</h1>
        </div>

        <div className="items-center justify-center h-14 w-full my-4">
          <label className="block text-gray-600 text-sm font-normal">
            First Name
          </label>
          <input
            type="text"
            className="h-10 w-96 border mt-2 px-2 py-2"
            placeholder="Enter Your First Name Here"
            name="firstName"
            value={employee.firstName}
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div className="items-center justify-center h-14 w-full my-4">
          <label className="block text-gray-600 text-sm font-normal">
            Last Name
          </label>
          <input
            type="text"
            className="h-10 w-96 border mt-2 px-2 py-2"
            placeholder="Enter Your Last Name Here"
            name="lastName"
            value={employee.lastName}
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div className="items-center justify-center h-14 w-full my-4">
          <label className="block text-gray-600 text-sm font-normal">
            Email Address
          </label>
          <input
            type="email"
            className="h-10 w-96 border mt-2 px-2 py-2"
            placeholder="Enter Your Email Here"
            name="emailId"
            value={employee.emailId}
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div className="items-center justify-center h-14 w-full my-5 space-x-4 pt-2">
          <button className="rounded text-white font-semibold bg-yellow-400 hover:bg-yellow-700 py-1 px-3">
            Cancel
          </button>
          <button
            onClick={updateEmployee}
            className="rounded text-white font-semibold bg-green-400 hover:bg-green-700 py-1 px-3"
          >
            Update
          </button>
        </div>
      </div>
    </div>
  );
};

export default UpdateEmployee;
