import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import EmployeeService from "../services/EmployeeService";

const EmployeeList = () => {
  const navigate = useNavigate();
  const [employees, setEmployees] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const response = await EmployeeService.getEmployees();
        setEmployees(response.data);
      } catch (error) {
        console.log(error);
      }
      setLoading(false);
    };
    fetchData();
  }, []);

  return (
    <div className="container mx-auto my-8">
      <div className="h-12">
        <button
          onClick={() => navigate("/addEmployee")}
          className="rounded bg-lime-600 text-white px-6 py-2 font-semibold"
        >
          Add Employee
        </button>
      </div>
      <div className="flex shadow border-b">
        <table className="min-w-full">
          <thead className="bg-gray-500">
            <tr>
              <th className="text-left font-medium text-white uppercase tracking-wider py-3 px-6">
                First Name
              </th>
              <th className="text-left font-medium text-white uppercase tracking-wider py-3 px-6">
                Last Name
              </th>
              <th className="text-left font-medium text-white uppercase tracking-wider py-3 px-6">
                Email ID
              </th>
              <th className="text-left font-medium text-white uppercase tracking-wider py-3 px-6">
                Actions
              </th>
            </tr>
          </thead>
          {!loading && (
            <tbody className="bg-white">
              {employees.map((employee) => (
              <tr key={employee.id}>
                <td className="text-left px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-amber-700">{employee.firstName}</div>
                </td>
                <td className="text-left px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-lime-600">{employee.lastName}</div>
                </td>
                <td className="text-left px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-blue-800">{employee.emailId}</div>
                </td>
                <td className="text-left px-3 py-4 whitespace-nowrap font-medium text-sm">
                  <a
                    href="#"
                    className="text-indigo-600 hover:text-indigo-800 hover:bg-blue-200 px-2"
                  >
                    EDIT
                  </a>
                  <a
                    href="#"
                    className="text-red-600 hover:text-red-800 hover:bg-red-200 px-2"
                  >
                    DELETE
                  </a>
                </td>
              </tr>
              ))}
            </tbody>
          )}
        </table>
      </div>
    </div>
  );
};

export default EmployeeList;
